import React from 'react';
import $ from 'jquery';

class News extends React.Component {

    constructor(props) { 
        super(props);
        this.state = { news: [{},{}] };
    }

    componentDidMount() {
        this.getNews();
    }

    getNews() {

        $.get("https://cointelegraph.com/rss",function(data) {    

            var $xml = $(data);   
            var items = [];

            $xml.find("item").each(function() {
                var $this = $(this);
                console.log($this.find("title").text());
                items.push({
                    title: $this.find("title").text(),
                    link: $this.find("link").text(),
                    description: $this.find("description").text(),
                    pubDate: $this.find("pubDate").text(),
                });
            });

            this.setState({ news: items });

        }.bind(this),'xml');    
    }

    render() { 
        return (
            <div className="News">
              <div className="App-feeds">
              </div>
              <div className="panel-list">
                  <a href={this.state.news[1].link}>{this.state.news[1].title}</a>
                  <br/><small>{this.state.news[1].pubDate}</small>
              </div>
            </div>
        );
    }
}

export default News;
