import './App.css';
import React from 'react';
import News from './News.js';

class App extends React.Component {

    constructor(props) { 
        super(props);
        this.state.nodeid = "";
    }

    componentDidMount() {
        this.getNodeID();
    }

    getNodeID() {

        fetch("http://127.0.0.1:8001/node")
            .then(response => response.json())
            .then((jsonData) => {
                // jsonData is parsed json object received from url
                this.state.nodeid = jsonData.message;
                console.log(this.state.nodeid);
            })
            .catch((error) => {
                // handle your errors here
                console.error(error);
            });
    }

    render() { 
        return (
            <div className="container-lg">
                <div className="row g-3">
                    <div className="col-4">
                        <div className="p-2 border bg-white">
                            <b>Node: {this.state.nodeid}</b><br/>
                            Blocks forged: 24<br/>
                            Node IP: 192.168.1.145<br/>
                            Node Port: 10001<br/>
                            Node API Port: 8001<br/>
                            Master Node: 192.168.1.15<br/><br/>
                            <div className="fleft">
                            <button className="btn btn-primary">Configure Node</button></div> 
                            <div className="fright">
                            <button className="btn btn-primary">Stop Node</button></div>
                            <div className="clear"></div>
                        </div>
                    </div>
                    <div className="col-4">
                        <div className="p-2 border bg-white">Custom column padding 2</div>
                    </div>
                    <div className="col-4">
                        <div className="p-2 border bg-white">Custom column padding 3</div>
                    </div>
                </div>

                <div className="row g-3">
                    <div className="col-4">
                        <div className="p-2 border bg-white news">
                            <b>Crypto News:</b><br/>
                            <News/>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default App;
