import './App.css';
import React from 'react';
import News from './News.js';
import Console from './Console.js';

class App extends React.Component {

    constructor(props) { 
        super(props);
        this.state = { nodeid: "", ip: "", port: "", master: "" };
    }

    componentDidMount() {
        this.getNodeID();
    }

    getNodeID() {

        fetch("http://127.0.0.1:8001/node")
            .then(response => response.json())
            .then((jsonData) => {
                // jsonData is parsed json object received from url
                this.setState({ nodeid: jsonData.message });
                this.setState({ ip: jsonData.ip });
                this.setState({ port: jsonData.port });
                this.setState({ master: jsonData.master });
                console.log(this.state.nodeid);
            })
            .catch((error) => {
                // handle your errors here
                console.error(error);
            });
    }

    render() { 
        return (
            <div className="container-lg">
                    <div className="col-4">
                        <div className="p-2 border bg-white info">
                            <b>Node: {this.state.nodeid}</b><br/>
                            Node IP: {this.state.ip}<br/>
                            Node Port: {this.state.port}<br/>
                            Node API Port: {this.state.port ? 8001 : null}<br/>
                            Master Node: {this.state.master}<br/><br/>
                            <div className="fleft">
                            <button className="btn btn-primary">Configure Node</button></div> 

                        </div>
                    <div className="col-4">
                        <div className="p-2 border bg-white">
                            <b>Node Console</b>
                            <Console/>
                        </div>
                    </div>
                    <div className="col-4">
                        <div className="p-2 border bg-white">Custom column padding 3</div>
                    </div>
                </div>

                    <div className="col-4">
                        <div className="p-2 border bg-white news">
                            <b>Crypto News:</b><br/>
                            <News/>
                        </div>
                    </div>
            </div>
        );
    }
}

export default App;
