import React from 'react';

function BuySellTransfer() {   

    var jsonData = {
      "users": [
          {
              "name": "alan", 
              "age": 23,
              "username": "aturing"
          },
          {
              "name": "john", 
              "age": 29,
              "username": "__john__"
          }
      ]
    }

    function handleClick() {

      // Send data to the backend via POST
      fetch('http://------------:8080/', {  // Enter your IP address here
        method: 'POST', 
        mode: 'cors', 
        body: JSON.stringify(jsonData) // body data type must match "Content-Type" header

      })

    }

    function showBuyForm() {
        let info=document.querySelector('.buyForm')
        info.style.display="none"
        document.getElementById('buyForm').style.display="block"
    }

    function hideBuyForm() {
        let info=document.querySelector('.buyForm')
        info.style.display="block"
        document.getElementById('buyForm').style.display="none"
    }

    function showSellForm() {

    }

    function showTXForm() {

    }

    function showStakeForm() {

    }


    return (
    <div>
        <div className="transactionSpace">
            <div className="buyForm" style="display:none;">
                <div className="form-group mx-sm-3 mb-2">
                    <label for="inputPassword2" class="sr-only">Password</label>
                    <input type="password" className="form-control" id="inputPassword2" placeholder="Password" />
                </div>
                <button type="submit" class="btn btn-primary mb-2">Purchase</button>
            </div>
        </div>
        <table>
        <tbody>
        <tr>
        <td>
        <button className="btn btn-primary" onClick={showBuyForm}>
            Buy
        </button>
        </td>
        <td>
        <button className="btn btn-primary" onClick={showSellForm}>
            Sell
        </button>
        </td>
        <td>
        <button className="btn btn-primary" onClick={showTXForm}>
            Transfer
        </button>
        </td>
        <td>
        <button className="btn btn-primary" onClick={showStakeForm}>
            Stake
        </button>
        </td>
        </tr>
        </tbody>
        </table>
    </div>
    );

  }

  export default BuySellTransfer;
