import React from 'react';
import $ from 'jquery';

class News extends React.Component {

    constructor(props) { 
        super(props);
        this.state = { news: [{},{}] };
    }

    componentDidMount() {
        this.getNews();
    }

    getNews() {

        $.get("https://news.bitcoin.com/feed/",function(data) {    

            var $xml = $(data);   
            var items = [];

            $xml.find("item").each(function() {
                var $this = $(this);
                items.push({
                    title: $this.find("title").text(),
                    link: $this.find("link").text(),
                    description: $this.find("description").text(),
                    pubDate: $this.find("pubDate").text(),
                });
            });

            console.log(items);
            this.setState({ news: items });

        }.bind(this),'xml');
    }

    render() { 
        return (
            <div className="News">
              <div className="App-feeds">
              </div>
              <div className="panel-list">
              <a href={this.state.news[0] ? this.state.news[0].link : null}>{this.state.news[0] ? this.state.news[0].title : null}</a><br/><small>{this.state.news[0] ? this.state.news[0].pubDate : null}</small><br/><br/>

                  <a href={this.state.news[1] ? this.state.news[1].link : null}>{this.state.news[1] ? this.state.news[1].title : null}</a><br/><small>{this.state.news[1] ? this.state.news[1].pubDate : null}</small><br/><br/>

                  <a href={this.state.news[2] ? this.state.news[2].link : null}>{this.state.news[2] ? this.state.news[2].title : null}</a><br/><small>{this.state.news[2] ? this.state.news[2].pubDate : null}</small><br/><br/>

                  <a href={this.state.news[3] ? this.state.news[3].link : null}>{this.state.news[3] ? this.state.news[3].title : null}</a><br/><small>{this.state.news[3] ? this.state.news[3].pubDate : null}</small><br/><br/>

                  <a href={this.state.news[4] ? this.state.news[4].link : null}>{this.state.news[4] ? this.state.news[4].title : null}</a><br/><small>{this.state.news[4] ? this.state.news[4].pubDate : null}</small><br/><br/>
              </div>
            </div>
        );
    }
}

export default News;
