import './App.css';
import News from './News.js';

var app = express();

app.use(function(req, res, next) {
  res.header("Access-Control-Allow-Origin", "*");
  res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
  next();
});

function App() {
  return (
    <div className="container-lg">
        <div className="row g-3">
            <div className="col-4">
                <div className="p-2 border bg-white">
                    <b>Node: 17cb7381-4db6-41de-acf3-1aba00c86386</b><br/>
                    Status: Up<br/>
                    Blocks forged: 24<br/>
                    Node IP: 192.168.1.145<br/>
                    Node Port: 10001<br/>
                    Node API Port: 8001<br/>
                    Master Node: 192.168.1.15<br/><br/>
                    <div className="fleft">
                    <button className="btn btn-primary">Configure Node</button></div> 
                    <div className="fright">
                    <button className="btn btn-primary">Stop Node</button></div>
                    <div className="clear"></div>
                </div>
            </div>
            <div className="col-4">
                <div className="p-2 border bg-white">Custom column padding 2</div>
            </div>
            <div className="col-4">
                <div className="p-2 border bg-white">Custom column padding 3</div>
            </div>
        </div>

        <div className="row g-3">
            <div className="col-4">
                <div className="p-2 border bg-white news">
                    <b>Crypto News:</b><br/>
                    <News/>
                </div>
            </div>
        </div>
    </div>
  );
}

export default App;
